import React from 'react';

const Votacao = ({children}) => {

     const apurarVotos = () => {
          //let cidadeVencedora = children.forEach(element => document.getElementById(element.id))
          let cidadeVencedora = children.map(element => document.getElementById(element.id)).reduce((maior, atual) => {
               if(maior.quantidadeVotos < atual.quantidadeVotos) {
                    maior = atual.quantidadeVotos;
               }
               return maior;
          });
          document.getElementById("cidade_vencedora").innerHTML = cidadeVencedora.nome;
     }

     return (
          <div>
               {children}
               <button onClick={apurarVotos}>Apurar Votos</button>

               <br/>
               <p>Cidade Vencedora: </p>
               <a id="cidade_vencedora"></a>
          </div>
     );
};

export default Votacao;
