import React from 'react';

const Votacao = ({children}) => {

     const apurarVotos = () => {
          let empate = false;
          let cidadeVencedora = children
          .map(element => {
               if(element.hasOwnProperty("props") && element.props.hasOwnProperty("nome")) {
                    return document.getElementById(element.props.nome)
               }
          })
          .reduce((maior, atual) => {
               if(maior.value < atual.value) {
                    maior = atual;
               }
               else if(maior.value == atual.value) {
                    empate = true;
               }
               return maior;
          });
          if(empate) {
               document.getElementById("resultado").innerHTML = "Empate!";
          }
          else {
               document.getElementById("resultado").innerHTML = cidadeVencedora.id;
          }
     }

     return (
          <div>
               {children}
               <button onClick={apurarVotos}>Apurar Votos</button>

               <br/>
               <p>Cidade Vencedora: </p>
               <a id="resultado"></a>
          </div>
     );
};

export default Votacao;
