import React from 'react';
import { render } from 'react-dom';

const Votacao = ({children}) => {

     let renderizarChildren = render

     const apurarVotos = () => {

          let empate = false;

          let cidadeVencedora = children.filter(element => {
               return element.hasOwnProperty("props") && element.props.hasOwnProperty("nome")
          }).map(element => {
               return document.getElementById(element.props.nome)
          }).reduce((maior, atual) => {
               empate = (maior.value == atual.value);
               if(maior.value < atual.value) {
                    maior = atual;
                    empate = false;
               }
               return maior;
          });

          if(empate) {
               document.getElementById("resultado").innerHTML = "Houve um Empate!";
          }
          else {
               document.getElementById("resultado").innerHTML = cidadeVencedora.id;
          }
     }

     (function() {
          children.filter(element => {
               return element.hasOwnProperty("props") && element.props.hasOwnProperty("nome")
          }).forEach(element => {
               if(element.props.mostrarQuantidade) {
                    document.getElementById("quantidade").style.display = 'none';
               }
          });
     })();

     return (
          <div>
               {children}
               <button onClick={apurarVotos}>Apurar Votos</button>

               <br/>
               <p>Cidade Vencedora: </p>
               <a id="resultado"></a>
          </div>
     );
};

export default Votacao;
